import React, {useEffect, useState} from 'react';
import AuthContext from './AuthContext';
import axios from 'axios';
import jwtDecode from 'jwt-decode';
import AsyncStorage from '@react-native-async-storage/async-storage';
import {
    loadTokenFromLocalStorage ,
    saveTokenToLocalStorage,
    loadUserDataFromLocalStorage,
    saveUserDataToLocalStorage,
    deleteTokenFromLocalStorage
    } from '../service/AsyncStorage';







export default function ( {children} ) {
    const tokenFromStorage = async () =>{
        const value = await AsyncStorage.getItem('ACCESS_TOKEN');
        console.log(value + " Das Token")
        if(value){
            return value;
        }
    }

    const userFromStorage = async () =>{
        const jsonValue = await AsyncStorage.getItem("USER_DATA");
        console.log(jsonValue)
        if(jsonValue){
            return jsonValue;
        }
    }


    const [token, setToken] = useState(tokenFromStorage);
    const [userData, setUserData] = useState(userFromStorage);
    console.log(JSON.parse(userData) + " UserData")


    useEffect(() => {
        if (token) {
            try {
                const decoded = jwtDecode(token);
                console.log(decoded.toString() + " value of decoded token")
                console.log(decoded.user)
                if (decoded.exp > new Date().getTime() / 1000) {
                    setUserData(decoded);
                    saveTokenToLocalStorage(token);
                    saveUserDataToLocalStorage(decoded);
                }
            } catch (e) {
                console.log(e);
            }
        }
    }, [token]);

    const tokenIsValid = () =>
        token && userData?.exp > new Date().getTime() / 1000;

    const loginWithUserCredentials = (loginData) =>
        axios
            .post('http://192.168.178.76:8080/auth/login', loginData)
            .then((response) => {
                setToken(response.data)
            });

    const logout = () =>
        deleteTokenFromLocalStorage();

    console.log(tokenIsValid() + " " + token)
    return (
        <AuthContext.Provider
            value={{
                token,
                setToken,
                logout,
                tokenIsValid,
                loginWithUserCredentials,
                userData,
            }}
        >
            {children}
        </AuthContext.Provider>
    );
}
