import React, {useContext, useEffect, useState} from 'react';
import {Grid, Row, Col} from "react-native-easy-grid";
import { ListItem, SearchBar } from 'react-native-elements';
import {
    StyleSheet,
    Text,
    View,
    FlatList,
    ActivityIndicator,
    Image
} from 'react-native';
import LiveSearchContext from "../context/LiveSearchContext";
import AddMealSuche from "./AddMealSuche";




export default function AddMealList() {

    const {liveSearchData} = useContext(LiveSearchContext);

    return (
        <Row size={7}>
                <FlatList
                    data={liveSearchData}
                    renderItem={({ item }) => (
                        <ListItem
                            roundAvatar
                            title={`${item.product_name}`}
                            subtitle={item.brands}
                            leftAvatar={{ source: { uri: item.image_front_thumb_url } }}
                            placeholder={<ActivityIndicator/>}
                            containerStyle={{ borderBottomWidth: 0 }}
                        />
                    )}
                    keyExtractor={item => item.email}
                    ItemSeparatorComponent={renderSeparator}
                    ListHeaderComponent={AddMealSuche}
                />

        </Row>
    )


}


const styles = StyleSheet.create({
    container: {
        backgroundColor: '#f8f8f8',
        alignItems: 'center'
    },
    text: {
        fontSize: 20,
        color: '#101010',
        marginTop: 60,
        fontWeight: '700'
    },
    listItem: {
        marginTop: 10,
        paddingVertical: 20,
        paddingHorizontal: 20,
        backgroundColor: '#fff',
        flexDirection: 'row'
    },
    coverImage: {
        width: 100,
        height: 100,
        borderRadius: 8
    },
    metaInfo: {
        marginLeft: 10
    },
    title: {
        fontSize: 18,
        width: 200,
        padding: 10
    }
});

const renderSeparator = () => {
    return (
        <View
            style={{
                height: 1,
                width: '86%',
                backgroundColor: '#CED0CE',
                marginLeft: '14%',
            }}
        />
    );
};
