import React from 'react';
import { StyleSheet, View, Text } from 'react-native'
import styled from 'styled-components/native'
import { Col, Row, Grid } from "react-native-easy-grid";
import {moderateScale, scale, verticalScale} from "../styles/globalstyles";
import LinearGradient from 'react-native-linear-gradient';




export default function DashboardHeader(){

    return(

        <LinearGradient
            start={{x: 0.0, y: 0.25}} end={{x: 0.5, y: 1.0}}
            locations={[0,0.5,0.6]}
            colors={['#4c669f', '#3b5998', '#192f6a']}
            style={GradientStyle.linearGradient}>
            <Row size={1} style={DashboardStyle.container}>
                <Text style={{paddingTop:20, color:'white'}}>
                    Dashboard
                </Text>

                <Row style={DashboardStyle.cols} >
                    <Col size={1}>
                        <View>
                            <Text style={DashboardStyle.textStyle}>
                                Col1
                            </Text>
                        </View>
                    </Col>
                    <Col size={1}>
                        <View>
                            <Text style={DashboardStyle.textStyle}>
                                Col2
                            </Text>
                        </View>
                    </Col>
                    <Col size={1}>
                        <View>
                            <Text style={DashboardStyle.textStyle}>
                                Col3
                            </Text>
                        </View>
                    </Col>
                </Row>
            </Row>
        </LinearGradient>
    )
}




const DashboardStyle = StyleSheet.create({
        container: {

            backgroundColor: 'blue'
        },
        cols: {

            display: 'flex',
            alignItems: 'center',
            flexDirection: 'row',
            backgroundColor: 'blue',
            justifyContent:'space-evenly',
        },
    textStyle:{
        fontSize: moderateScale(16,0.4),
        color:'azure',
        alignContent: 'center',
    }
    }
)


const GradientStyle = StyleSheet.create({
    linearGradient: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        borderRadius: 5
    }
});
