import React from 'react';
import { StyleSheet, View, Text } from 'react-native'
import styled from 'styled-components/native'
import { Col, Row, Grid } from "react-native-easy-grid";
import {moderateScale, scale, verticalScale} from "../styles/globalstyles";
import { LinearGradient } from 'expo-linear-gradient';




export default function DashboardHeader(){

    return(
<View>
    <LinearGradient style={{
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        height: "100%",
    }} colors={['#5f69c1', '#374392' ]} />


            <Row size={1} style={DashboardStyle.container}>

                <Text style={{paddingTop:20, color:'white'}}>
                    Dashboard
                </Text>

                <Row style={DashboardStyle.cols} >
                    <Col size={1}>
                        <View>
                            <Text style={DashboardStyle.textStyle}>
                                Col1
                            </Text>
                        </View>
                    </Col>
                    <Col size={1}>
                        <View>
                            <Text style={DashboardStyle.textStyle}>
                                Col2
                            </Text>
                        </View>
                    </Col>
                    <Col size={1}>
                        <View>
                            <Text style={DashboardStyle.textStyle}>
                                Col3
                            </Text>
                        </View>
                    </Col>
                </Row>
            </Row>
</View>
    )
}




const DashboardStyle = StyleSheet.create({
        container: {

            backgroundColor: 'blue'
        },
        cols: {

            display: 'flex',
            alignItems: 'center',
            flexDirection: 'row',
            backgroundColor: 'blue',
            justifyContent:'space-evenly',
        },
    textStyle:{
        fontSize: moderateScale(16,0.4),
        color:'azure',
        alignContent: 'center',
    }
    }
)


const GradientStyle = StyleSheet.create({
    linearGradient: {
        flex: 1,
        paddingLeft: 15,
        paddingRight: 15,
        borderRadius: 5
    }
});
