import {Grid, Row, Col} from "react-native-easy-grid";
import React, {useState} from 'react';
import { StyleSheet, ScrollView, Text, View} from 'react-native'
import styled from 'styled-components/native'
import { LinearGradient } from 'expo-linear-gradient';
import FloatingAddButton from "../navigation/FloatingAddButton";
import {moderateScale} from '../styles/globalstyles';



const BreakfastBox = () => {

    const [currentBreakfast, setCurrentBreakfast] = useState({
        name: "Omelet",
        ingredients: ["Egg", "Milk", "Cheddar Cheese"],
        allergens: ["Eggs","Lactose"],
        sideEffects: {hasSideEffect: true,
            whichSideEffect: {feelingState:3, headache: 2, stomach: 3},
            sideEffectsNotes: "Ich habe mich unwohl gefühlt und dazu Kopfschmerzen bekommen. Auch mein Margen spielte verrückt."}}
    );

    return (
            <StyledMealBox>
                <Grid>
                    <Row>
                        <Col size={1}><Text>Bild</Text></Col>
                        <Col size={4}><Text>FRÜHSTÜCK Hinzufügen</Text></Col>
                        <Col size={1}><Text>+</Text></Col>
                    </Row>
                    <Row>
                        <Col><Text>Omlette</Text></Col>
                        <Col><Text>Nebenwirkung:</Text></Col>
                    </Row>
                    <Row>
                        <Col><Text>Allergene: Milch, Ei</Text></Col>
                        <Col><Text>Scale</Text></Col>
                    </Row>
                </Grid>
            </StyledMealBox>
    )
}






export default function DashboardList(){
        return(
                <Row size={2}>
                        <ScrollView>
                            <Grid style={StyledGridList.container}>
                                <Row>
                                    <BreakfastBox/>
                                </Row>
                                <Row>
                                    <Text> Mittag</Text>
                                </Row>
                                <Row>
                                    <Text> Abend</Text>
                                </Row>
                                <Row>
                                    <Text> Snack</Text>
                                </Row>
                            </Grid>
                        </ScrollView>
                </Row>


        );
}


const StyledGridList = StyleSheet.create({
    container: {
        flexDirection: 'column'
    }
})


const StyledMealBox = styled.View`
    width:100%;
    marginLeft: ${moderateScale(5, 0.2)};
    padding: ${moderateScale(7, 0.2)}, ${moderateScale(7, 0.2)}, ${moderateScale(7, 0.2)}, ${moderateScale(7, 0.2)};
    min-height: ${moderateScale(125, 0.3)};
    border-color: #a5a5a5;
    borderWidth: 1px;

`
